mapper.select:
select
  id, user_id, title, description, content, created, status
from m_blog


        and id = #{id}


        and user_id = #{userId}


        and title = #{title}


        and description = #{description}


        and content = #{content}


        and created = #{created}


        and status = #{status}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from m_blog


        and id = #{id}


        and user_id = #{userId}


        and title = #{title}


        and description = #{description}


        and content = #{content}


        and created = #{created}


        and status = #{status}
