mapper.select:
select
  id, username, avatar, email, password, status, created, last_login
from m_user


        and id = #{id}


        and username = #{username}


        and avatar = #{avatar}


        and email = #{email}


        and password = #{password}


        and status = #{status}


        and created = #{created}


        and last_login = #{lastLogin}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from m_user


        and id = #{id}


        and username = #{username}


        and avatar = #{avatar}


        and email = #{email}


        and password = #{password}


        and status = #{status}


        and created = #{created}


        and last_login = #{lastLogin}
